import type { VercelRequest, VercelResponse } from '@vercel/node'
import { createClerkClient } from '@clerk/backend'

const clerk = createClerkClient({ secretKey: process.env.CLERK_SECRET_KEY || '' })

export async function verifyClerk(req: VercelRequest, res: VercelResponse) {
  try {
    const authHeader = req.headers.authorization || ''
    const token = authHeader.replace(/^Bearer\s+/i, '')
    if (!token) { res.status(401).json({ error: 'missing token' }); return null }
    const session = await clerk.sessions.verifyToken(token, { secretKey: process.env.CLERK_SECRET_KEY || '' })
    return { userId: session.userId }
  } catch (e) {
    res.status(401).json({ error: 'unauthorized' }); return null
  }
}

import type { VercelRequest, VercelResponse } from '@vercel/node'
import { verifyToken } from '@clerk/backend'

export async function verifyClerk(req: VercelRequest, res: VercelResponse) {
  try {
    const authHeader = req.headers.authorization || ''
    const token = authHeader.replace(/^Bearer\s+/i, '')
    if (!token) { res.status(401).json({ error: 'missing token' }); return null }
    const payload = await verifyToken(token, { secretKey: process.env.CLERK_SECRET_KEY || '' })
    return { userId: payload.sub }
  } catch (e) {
    res.status(401).json({ error: 'unauthorized' }); return null
  }
}

import { createApp, h } from 'vue'
import { createPinia } from 'pinia'
import App from './App.vue'
import router from './router'
import './app.css'

import { Clerk } from '@clerk/clerk-js'
const clerkPubKey = import.meta.env.NEXT_PUBLIC_CLERK_PUBLISHABLE_KEY
const clerk = new Clerk(clerkPubKey || 'pk_test_replace_me')
await clerk.load()
;(window as any).Clerk = clerk

const app = createApp({ render: () => h(App) })
app.use(createPinia())
app.use(router)
app.mount('#app')

if ('serviceWorker' in navigator) {
  window.addEventListener('load', () => {
    navigator.serviceWorker.register('/service-worker.js').catch(console.error)
  })
}

